function getMean(arr: number[]) {
    var sum = 0;
    //loop and sum all elements in array
    for (var i in arr) {
        sum += arr[i];
    }

    //Get the length of the array
    var n = arr.length;

    //Return the average / mean.
    return (sum / n);
}

function getVariancePopulation(arr: number[]) {
    var mean = getMean(arr)
    var sum_squared_differences = 0;
    for (var i in arr) {
        sum_squared_differences += (arr[i] - mean) ** 2;
    }
    var n = arr.length;
    //Return the average / mean.
    return (sum_squared_differences / n);
}

function getVarianceSample(arr: number[]) {
    var mean = getMean(arr)
    var sum_squared_differences = 0;
    for (var i in arr) {
        sum_squared_differences += (arr[i] - mean) ** 2;
    }
    var n = arr.length;
    //Return the average / mean.
    return (sum_squared_differences / (n - 1));
}

function getStdevPopulation(arr: number[]) {
    //return sqrt of the population variance
    return Math.sqrt(getVariancePopulation(arr));
}

function getStdevSample(arr: number[]) {
    //return sqrt of the sample variance 
    return Math.sqrt(getVarianceSample(arr));
}

function printSummary(arr: number[], sample = false) {
    if (sample) {
        console.log(`Mean:                  ${getMean}`)
        console.log(`Variance:              ${getVarianceSample}`)
        console.log(`Standard Deviation:    ${getStdevSample}`)
    }
    console.log(`Mean:                  ${getMean}`)
    console.log(`Variance:              ${getVariancePopulation}`)
    console.log(`Standard Deviation:    ${getStdevPopulation}`)
}
